package aaa;

public class PersonEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Person p = new Person();
		
		p.setAge(40);
		p.setName("James");
		p.setMarry(true);
		p.setChild(3);
		
		System.out.println(p.getAge());
		System.out.println(p.getName());
		System.out.println(p.isMarry());
		System.out.println(p.getChild());
		

	}

}
